components/amp/css/product:
.productView {
    margin-left: auto;
    margin-right: auto;
    max-width: 91.71429rem;
    padding: 1rem 1.5rem;
}

.productView-images {
    padding: 2rem 0;
}

.productView-action {
    margin-bottom: 2rem;
}

.productAccordion-title {
    background-color: #f2f2f2;
    border-right: 0;
    border-left: 0;
    padding: .75rem 1.5rem;
    margin-bottom: -1px;
    font-size: 1rem;
    text-transform: uppercase;
    position: relative;
}

.productAccordion-title:after {
    content: '';
    display: block;
    width: 16px;
    height: 16px;
    background-image: url('data:image/svg+xml;utf8,<svg version="1.1" id="Layer_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" width="24px" height="24px" viewBox="0 0 24 24" enable-background="new 0 0 24 24" xml:space="preserve"><polygon points="23,11 13,11 13,1 11,1 11,11 1,11 1,13 11,13 11,23 13,23 13,13 23,13 "/></svg>');
    background-size: 100%;
    position: absolute;
    top: 50%;
    right: .75rem;
    transform: translateY(-50%);
    transition: transform .2s ease;
}

amp-accordion > section[expanded] > .productAccordion-title:after {
    transform: translateY(-50%) rotate(45deg);
}

.productAccordion-content {
    padding: 1rem 0;
}

.socialLinks {
    margin: 1rem 0 0;
    padding: 0;
    text-align: center;
}

.socialLinks-item {
    margin: 0;
    padding: 0;
    display: inline-block;
    font-size: 0;
    margin-bottom: .35714rem;
    margin-right: .78571rem
}
.socialLinks-item:last-child {
    margin-right: 0
}


.writeReview-productDetails .product-brand {
    color: #a5a5a5;
    margin: 0
}
.writeReview-productDetails .product-title {
    margin-top: 0
}
.account--addReturn .table {
    display: block;
    margin-bottom: 3rem
}
.quickView:before,
.quickView:after {
    content: " ";
    display: table
}
.quickView:after {
    clear: both
}
.productReviews-list {
    list-style: none;
    margin: 0 -.75rem;
    max-width: none;
    width: auto;
    font-size: 0
}
.productReviews-list ul,
.productReviews-list ol {
    list-style: none;
    margin-bottom: 0
}
.productReviews-list:before,
.productReviews-list:after {
    content: " ";
    display: table
}
.productReviews-list:after {
    clear: both
}
.productReview {
    padding-left: .75rem;
    padding-right: .75rem;
    width: 100%;
    float: left;
    display: inline-block;
    float: none;
    font-size: 1rem;
    vertical-align: top
}
.productReview-title {
    margin: .5rem 0;
    text-transform: none
}
.productReview-author {
    color: #989898;
    margin: .5rem 0 1rem
}
.productReview-rating {
    display: inline-block;
    margin-bottom: .35714rem
}
.productReview-ratingNumber {
    border: 0;
    clip: rect(1px, 1px, 1px, 1px);
    height: 1px;
    overflow: hidden;
    padding: 0;
    position: absolute;
    width: 1px
}
.productView-details {
    margin-bottom: 1rem
}
.productView-product >:last-child {
    margin-bottom: 0
}
.productView-title {
    font-size: 16px;
    margin: -.78571rem 0 .35714rem
}
.productView-brand {
    font-family: Karla, Arial, Helvetica, sans-serif;
    color: #a5a5a5;
    font-size: 14px;
    font-weight: normal;
    margin: 0 0 .78571rem
}
.productView-brand a {
    color: #989898
}
.productView-brand a:hover {
    color: #4f4f4f
}
.productView-price {
    font-size: 15px;
    margin-bottom: .5rem
}
.productView-price .price-section--saving {
    font-size: 15px
}
.productView-price abbr {
    border: 0;
    color: inherit;
    font-size: inherit
}
.productView-info {
    margin-top: .78571rem
}
.productView-info >:last-child {
    margin-bottom: 0
}
.productView-info-name,
.productView-info-value {
    display: inline;
    margin: 0;
}
.productView-info-name {
    margin-right: 5px;
}
.productView-info-value::after {
    content: "\A";
    white-space: pre;
}

.productView-addition-table {
    margin-top: 1rem;
}

.alertBox {
    margin-bottom: .78571rem;
    padding: .78571rem 1.14286rem;
    background-color: #e8e8e8;
    border-radius: 4px
}

.alertBox .icon {
    height:1.42857rem;
    width:1.42857rem
}

.alertBox--success {
    background-color:#d5ffd8;
    border-color:#d5ffd8;
}

.alertBox--success svg {
    fill:#69d66f
}

.alertBox--error{
    background-color:#fdd;
    border-color:#fdd
}

.alertBox--error svg {
    fill:#ff7d7d
}

.alertBox-column{
    display:table-cell;
    vertical-align:middle
}

.alertBox-icon{
    text-align: center;
    padding-right: 1.14286rem
}

.alertBox-message {
    margin: 0;
    width: 100%
}

[data-custom-tab-title] {
    font-weight: bold;
    text-transform: uppercase;
    border-top: 1px solid #dcdcdc;
    padding-top: 1.5rem;
}
