{{#with theme_settings}}
    {{#or show_accept_amex show_accept_discover show_accept_mastercard show_accept_paypal show_accept_visa}}
        <div class="footer-payment-icons">
            {{#if show_accept_amex}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-american-express{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_discover}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-discover{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_mastercard}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-mastercard{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_paypal}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-paypal{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_visa}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-visa{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_applepay}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-applepay{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_amazon}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-amazon{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_googlepay}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-googlepay{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_klarna}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-klarna{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
        </div>
    {{/or}}
{{/with}}

components/common/navigation-list:
{{#if children}}
<a class="navPages-action has-subMenu {{#if menu_type '==' 'vertical'}}navPages-action--vertical{{/if}}" href="{{url}}"
    {{#if menu_type '!=' 'vertical'}}data-instantload{{/if}}
    {{#if id '==' category.id}}data-current-category{{/if}}
    data-collapsible="navPages-{{id}}{{idSuffix}}"
    {{#if menu_type '!=' 'vertical'}}data-collapsible-disabled-breakpoint="medium"{{/if}}
    data-collapsible-disabled-state="open"
    data-collapsible-enabled-state="closed">
    {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
</a>
<div class="navPage-subMenu
        {{#if menu_type '==' 'vertical'}}
            navPage-subMenu--vertical
        {{else if menu_type '==' 'standard'}}
            navPage-subMenu--standard
        {{/if}}
    " id="navPages-{{id}}{{idSuffix}}" aria-hidden="true" tabindex="-1">
    {{#if theme_settings.navPages_banners}}
        {{#if menu_type '==' 'mega'}}
            <div class="navPage-subMenu-rightBanners" data-emthemesmodez-remote-banner="emthemesModezBannersBlockMegaMenuRight{{@index}}"></div>
        {{/if}}
    {{/if}}
    <ul class="navPage-subMenu-list">
        <li class="navPage-subMenu-item">
            <a class="navPage-subMenu-action navPages-action" href="{{url}}" data-instantload>{{lang 'category.view_all.name' category=name}}</a>
        </li>
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if children}}
                    <a
                        class="navPage-subMenu-action navPages-action has-subMenu"
                        href="{{url}}"
                        {{#if ../../menu_type '!=' 'vertical'}}data-instantload{{/if}}
                        {{#if id '==' ../../category.id}}data-current-category{{/if}}
                        data-collapsible="navPages-{{id}}{{../../idSuffix}}"
                        {{#if ../../menu_type '!=' 'vertical'}}data-collapsible-disabled-breakpoint="medium"{{/if}}
                        data-collapsible-disabled-state="open"
                        data-collapsible-enabled-state="closed">
                        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}{{../../idSuffix}}">
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action" href="{{url}}" data-instantload>{{lang 'category.view_all.name' category=name}}</a>
                        </li>
                        {{#each children}}
                        <li class="navPage-childList-item">
                            {{> components/common/navigation-list-child 
                                    category_id=../../../category.id 
                                    idSuffix=../../../idSuffix 
                                    theme_settings=../../../theme_settings
                                    menu_type=../../../menu_type}}
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action navPages-action" href="{{url}}" data-instantload {{#if id '==' ../../category.id}}data-current-category{{/if}}>{{name}}</a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
    {{#if theme_settings.navPages_banners}}
        {{#if menu_type '==' 'mega'}}
            <div class="navPage-subMenu-bottomBanners" data-emthemesmodez-remote-banner="emthemesModezBannersBlockMegaMenuBottom{{@index}}"></div>
        {{/if}}
    {{/if}}
</div>
{{else}}
<a class="navPages-action" href="{{url}}" data-instantload {{#if id '==' category.id}}data-current-category{{/if}}>{{name}}</a>
{{/if}}

components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<ul class="navUser-section">
    <li class="navUser-item">
        <a class="navUser-action navUser-action--currencySelector has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">{{{lang 'common.currency' code=currency_selector.active_currency_code}}} <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
        <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item">
                <a href="{{{switch_url}}}"
                   data-currency-code="{{{code}}}"
                   {{#if cart_currency_switch_url.length '>' 0 }}
                   data-cart-currency-switch-url="{{{cart_currency_switch_url}}}"
                   {{/if}}
                   data-warning="{{lang 'common.currency_switch_promotion'}}"
                >
                {{#if is_active}}
                <strong>{{name}}</strong>
                {{else}}
                {{name}}
                {{/if}}
                </a>
            </li>
            {{/each}}
        </ul>
    </li>
</ul>
{{/if}}

components/common/navigation-list-child:
{{#if children}}
    <a
        class="navPage-childList-action navPages-action has-subMenu" 
        href="{{url}}"
        {{#if menu_type '!=' 'vertical'}}data-instantload{{/if}}
        {{#if id '==' category_id}}data-current-category{{/if}}
        data-collapsible="navPages-{{id}}{{idSuffix}}"
        {{#if menu_type '!=' 'vertical'}}data-collapsible-disabled-breakpoint="medium"{{/if}}
        data-collapsible-disabled-state="open"
        data-collapsible-enabled-state="closed">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    <ul class="navPage-childList" id="navPages-{{id}}{{idSuffix}}">
        <li class="navPage-childList-item">
            <a class="navPage-childList-action navPages-action" data-instantload href="{{url}}">{{lang 'category.view_all.name' category=name}}</a>
        </li>
        {{#each children}}
            <li class="navPage-childList-item">
                {{> components/common/navigation-list-child 
                    category_id=../category_id 
                    idSuffix=../idSuffix 
                    theme_settings=../theme_settings
                    menu_type=../menu_type}}
            </li>
        {{/each}}
    </ul>
{{else}}
    <a class="navPage-childList-action navPages-action" data-instantload href="{{url}}" {{#if id '==' category_id}}data-current-category{{/if}}>{{name}}</a>
{{/if}}
